// import lib
import React from 'react';
import FormGroup from './bootstrap/FormGroup';

//create a component
class StudentForm extends React.Component{
    constructor(props){
        super(props);

        this.state = {
            firstName: '',
            lastName: '',
            cf: '',
            bDate: ''
        }
    }
    onChangeFields = (event) => {
        this.setState({[event.target.name]: event.target.value});
    }

    onSubmit = (event) => {
        event.preventDefault();
    }

    onFocus = (event) => {
        event.currentTarget.type = 'date';
        event.currentTarget.min = '1910-01-01'
        event.currentTarget.max = '2003-01-01'
    }

    onBlur = (event) => {
        event.currentTarget.placeholder = 'Es. 10/01/2000';
    }

    onClickDate = (event) => {
        console.log('click')
        event.currentTarget.focus();
    }

    render(){
        return (
            <form onSubmit={this.onSubmit}>
                <FormGroup>
                    <label htmlFor='firstNAme'>Enter student first name:</label>
                    <input 
                      className='form-control'
                      type='text' 
                      id='firstName' 
                      name='firstName' 
                      value={this.state.firstName} 
                      onChange={this.onChangeFields}
                      placeholder='Es. Mario'
                      required
                      />
                </FormGroup>
                <FormGroup>
                    <label htmlFor='lastNAme'>Enter student last name:</label>
                    <input 
                      className='form-control'
                      type='text' 
                      id='lasttName' 
                      name='lastName' 
                      value={this.state.lastName} 
                      onChange={this.onChangeFields}
                      placeholder='Es. Rossi'
                      required
                      />
                </FormGroup>
                <FormGroup>
                    <label htmlFor='cf'>Enter student cf:</label>
                    <input 
                      className='form-control'
                      type='text' 
                      id='cf' 
                      name='cf' 
                      value={this.state.cf} 
                      onChange={this.onChangeFields}
                      placeholder='Es. VCCRRT93R06F83A'
                      required
                      />
                </FormGroup>
                <FormGroup>
                    <label htmlFor='bDate'>Enter student date of birth:</label>
                    <input 
                      className='form-control dateclass placeholderclass'
                      type='text' 
                      id='bDate' 
                      name='bDate' 
                      value={this.state.bDate} 
                      onChange={this.onChangeFields}
                      placeholder='Es. 10/01/2000'
                      onFocus={this.onFocus}
                      onBlur={this.onBlur}
                      required
                      />
                </FormGroup>
            </form>
        );
    }
}

//export a component
export default StudentForm;
