//import lib
import React from 'react';
import { Link } from 'react-router-dom';

//create a component (this component work only with bootstrap)
class NavItem extends React.Component {

  constructor(props){
    super(props)

    this.state = {
      pageURI: window.location.pathname+window.location.search,
      liClassNAme: (this.props.path === this.state.pageURI) ? "nav-item active" : "nav-item",
      aClassName: this.props.disabled ? "nav-link disabled" : "nav-link"
    }
  }

    render(){

      return (
        <li className={liClassName}>
          <Link to={this.props.path} className={aClassName}>
            {this.props.name}
            {(this.props.path === pageURI) ? (<span className="sr-only">(current)</span>) : ''}
          </Link>
        </li>
      );
    }
  }

// export a component 
export default NavItem;
