//import lib
import React from 'react';
import Toggler from './Toggler';

//create a component
class Navigation extends React.Component {

  constructor(props) {
    super(props);
    this.state = {
      isToggleOn: false
    };
  }

  showToggler = (e) => {
    e.preventDefault();
    this.setState(prevState => {
      const mydiv = document.querySelector('.collapse');
      prevState ? mydiv.collapse('hide') : mydiv.collapse('show');
      return {isToggleOn: !prevState.isToggleOn}
    });
  }

  render() {
    const classToggler = 'collapse navbar-collapse'// + (this.state.isToggleOn ? ' show' : '');
    console.log(classToggler);
    return (
      <nav className={this.props.className}>
        <a className="navbar-brand" href="/">{this.props.navBrandName}</a>
        <Toggler onClick={this.showToggler}/>

        <div className={classToggler} id="navbarSupportedContent">
          <ul className="navbar-nav mr-auto">{this.props.children}</ul>
        </div>
      </nav>
    )
  }
}

//export a component
export default Navigation;
