//import lib
import React from 'react';
import Card from './card/Card';
import CardImage from './card/CardImage';
import CardBody from './card/CardBody';
import userLogo from '../img/user.png';

//create a component
const StudentCardList = ({students}) => {
    if (!students) {
        return <div>Loading...</div>;
    }
    const studentList = students.map((user) =>{
        return (
            <Card>
                <CardImage image={userLogo} altText='user logo'/>
                <CardBody>
                    <h5 className="card-title">{`${user.nome} ${user.cognome}`}</h5>
                    <p className="card-text">{`Anno in corso: ${user.anno_in_corso}`}</p>
                    <p className="card-text text-primary">{`Codice fiscale: ${user.cf}`}</p>
                    <p className="card-text"><small className="text-muted">Last updated 3 mins ago</small></p>
                </CardBody>
            </Card>
        );
    });
    return (<div>{studentList}</div>);
}

//export a component
export default StudentCardList;
